CREATE OR REPLACE FUNCTION man_obter_dia_prox_build ( cd_versao_p text, nr_seq_man_os_ctrl_desc_p bigint) RETURNS timestamp AS $body$
DECLARE


ie_aplicacao_w		man_os_ctrl_desc.ie_aplicacao%type;
ie_noturno_w      regra_versao_sp.ie_noturno%type;
next_date_w			  timestamp;


BEGIN

	select	ie_aplicacao
	into STRICT	ie_aplicacao_w
	from	man_os_ctrl_desc
	where	nr_sequencia = nr_seq_man_os_ctrl_desc_p;

    if (ie_aplicacao_w = 'H') then

        select ie_noturno
        into STRICT ie_noturno_w
        from regra_versao_sp
        where cd_versao = cd_versao_p
        and ie_aplicacao_w = 'H'
        and coalesce(ds_hotfix::text, '') = ''
        and trunc(clock_timestamp()) between trunc(dt_inicio_regra) and trunc(dt_termino_regra);

        if (ie_noturno_w = 'A') then
            next_date_w := clock_timestamp();

        end if;

    else
    
        select	min(dt_geracao)
        into STRICT	next_date_w
        from	man_versao_calendario
        where	cd_versao = cd_versao_p
        and		ie_aplicacao = ie_aplicacao_w
        and (trunc(dt_geracao) >= trunc(clock_timestamp()))
        and		coalesce(cd_build::text, '') = '';
        
    end if;

    return next_date_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
