CREATE OR REPLACE FUNCTION man_obter_ds_tipo_hist ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_tipo_p		varchar(80);


BEGIN
if (coalesce(nr_sequencia_p,0) > 0) then
	select	substr(obter_desc_expressao(a.cd_exp_tipo, a.ds_tipo),1,80)
	into STRICT	ds_tipo_p
	from	man_tipo_hist	a
	where	a.nr_sequencia	= nr_sequencia_p;
end if;

return ds_tipo_p;

end;
$body$
