CREATE OR REPLACE FUNCTION man_obter_dt_fim_story (nr_story_p bigint, dt_referencia_p timestamp default clock_timestamp()) RETURNS timestamp AS $body$
DECLARE


dt_atualizacao_nrec_w	desenv_atividade.dt_atualizacao_nrec%type;
			

BEGIN

select	max(dt_atualizacao_nrec)
into STRICT	dt_atualizacao_nrec_w
from (SELECT	dt_atualizacao_nrec
	from	desenv_atividade
	where	nr_story = nr_story_p
	and	cd_status = 4
	and	dt_atualizacao_nrec < dt_referencia_p
	order by dt_atualizacao_nrec desc) alias1 LIMIT 1;

return	dt_atualizacao_nrec_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
