CREATE OR REPLACE FUNCTION man_obter_email_executor (nr_seq_ordem_p bigint) RETURNS varchar AS $body$
DECLARE

 
 
nm_usuario_exec_w	varchar(4000);
ds_retorno_w	varchar(4000);

C01 CURSOR FOR 
 
SELECT	substr(obter_dados_usuario_opcao(nm_usuario_exec,'E'),1,255) 
from	man_ordem_servico_exec 
where	nr_seq_ordem	= nr_seq_ordem_p;


BEGIN 
 
open C01;
loop 
fetch C01 into 
	nm_usuario_exec_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
		if (length(ds_retorno_w|| ';' || nm_usuario_exec_w) < 4000) then 
			ds_retorno_w	:= substr(ds_retorno_w || ';' || nm_usuario_exec_w,1,4000);
		end if;
	end;
end loop;
close C01;
									 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
