CREATE OR REPLACE FUNCTION man_obter_estagio_data ( nr_ordem_servico_p bigint, dt_parametro_p timestamp) RETURNS bigint AS $body$
DECLARE


nr_seq_estagio_w			bigint;
dt_parametro_w			timestamp;


BEGIN

dt_parametro_w			:= trunc(dt_parametro_p,'dd') + 86399 / 86400;

select	max(nr_seq_estagio)
into STRICT	nr_seq_estagio_w
from	man_ordem_serv_estagio
where	nr_sequencia in (
	SELECT max(nr_sequencia)
	from	man_ordem_serv_estagio
	where	nr_seq_ordem		= nr_ordem_servico_p
	and	dt_atualizacao	<= dt_parametro_w);

if (coalesce(nr_seq_estagio_w::text, '') = '') then
	select	nr_seq_estagio
	into STRICT	nr_seq_estagio_w
	from	man_ordem_servico
	where	nr_sequencia		= nr_ordem_servico_p;
end if;

return	nr_seq_estagio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
