SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_estagio_projeto ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w 		varchar(100);
dt_cliente_teste_w	timestamp;
dt_fim_acordo_w		timestamp;
dt_fim_real_w		timestamp;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	coalesce(dt_fim_acordo, dt_versao_prev),
		trunc(dt_fim_real)
	into STRICT	dt_fim_acordo_w,
		dt_fim_real_w
	from	man_ordem_servico
	where	nr_sequencia = nr_sequencia_p;

	select	max(a.dt_atualizacao)
	into STRICT	dt_cliente_teste_w
	from	man_estagio_processo b,
		man_ordem_serv_estagio a
	where	b.nr_sequencia 	= 2
	and	b.nr_sequencia 	= a.nr_seq_estagio
	and 	a.nr_seq_ordem 	= nr_sequencia_p;


	if (dt_fim_real_w < dt_fim_acordo_w) then
		ds_retorno_w := obter_desc_expressao(289288);--'Entregue';
	elsif ((dt_cliente_teste_w <= dt_fim_acordo_w) or (coalesce(dt_fim_real_w::text, '') = '')) then
		ds_retorno_w := obter_desc_expressao(648501);--'Não concluída';
	elsif (dt_cliente_teste_w > dt_fim_acordo_w AND dt_fim_real_w IS NOT NULL AND dt_fim_real_w::text <> '') then
		ds_retorno_w := 'Entregues fora do acordo';

	else
		ds_retorno_w := 'Não entregue';
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
