SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_exige_hist_teste ( nr_seq_ordem_serv_p bigint, nr_seq_tipo_p bigint) RETURNS varchar AS $body$
DECLARE


ie_necessita_teste_ana_w	varchar(1) := 'N';
dt_ultimo_cliente_teste_w	timestamp;
dt_ativ_prog_w				timestamp;
dt_ordem_serv_w				timestamp;



BEGIN

	select	max(dt_ordem_servico)
	into STRICT	dt_ordem_serv_w
	from	man_ordem_servico
	where	nr_sequencia = nr_seq_ordem_serv_p;

	select	max(b.dt_atividade)
	into STRICT 	dt_ativ_prog_w
	from 	man_ordem_serv_ativ b
	where 	b.nr_seq_ordem_serv = nr_seq_ordem_serv_p
	and		b.nr_seq_funcao in (1288, 11, 551);

	--Verifica 	ultimo estÃ¡gio de testes
	select	max(e.dt_atualizacao)
	into STRICT 	dt_ultimo_cliente_teste_w
	from	man_ordem_serv_estagio	e
	where	e.nr_seq_ordem		= nr_seq_ordem_serv_p
	and		e.nr_seq_estagio	in (2,791);

	select	CASE WHEN COUNT(*) =0 THEN  'S'  ELSE 'N' END
	into STRICT	ie_necessita_teste_ana_w
	from	man_ordem_serv_tecnico a
	where	a.nr_seq_tipo = nr_seq_tipo_p
	and		a.nr_seq_ordem_serv = nr_seq_ordem_serv_p
	and (a.dt_liberacao >=	coalesce(dt_ativ_prog_w, dt_ordem_serv_w))
	and		(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '');

return	ie_necessita_teste_ana_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
