SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_hist_cliente (nr_seq_ordem_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


dt_historico_w	timestamp;
ds_retorno_w	varchar(20);


BEGIN


	select	MAX(DT_HISTORICO)
	into STRICT	dt_historico_w
	from	MAN_ORDEM_SERV_TECNICO
	where	nr_seq_ordem_serv	= nr_seq_ordem_p
	and	nr_seq_tipo		= 4
	and 	ie_origem		= 'E'
	and	nm_usuario		<> nm_usuario_p;


ds_retorno_w	:= to_char(dt_historico_w,'dd/mm/yyyy hh24:mi:ss');

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
