SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_hr_fim ( nm_usuario_p text) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w		timestamp := null;
hr_inicio_w			varchar(4);
hr_fim_w			varchar(4);
dt_base_w			timestamp;


BEGIN

select	hr_inicio,
		hr_fim
into STRICT	hr_inicio_w,
		hr_fim_w
from	man_horario_trabalho
where	to_char(clock_timestamp(), 'd') = ie_dia_semana
and		man_obter_se_hor_trab_lib(nr_sequencia, null, null, nm_usuario_p) = 'S'  LIMIT 1;

if ((hr_inicio_w)::numeric  > (hr_fim_w)::numeric ) then
	dt_base_w := clock_timestamp() + interval '1 days';
else
	dt_base_w := clock_timestamp();
end if;

select	to_date(max(to_char(dt_base_w, 'dd/mm/yyyy') || ' ' || lpad(hr_fim_w, 2, 0) || ':' || substr(hr_fim_w, 3, 4) || ':00'), 'dd/mm/yyyy hh24:mi:ss')
into STRICT 	dt_retorno_w
;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
