CREATE OR REPLACE FUNCTION man_obter_hr_inicio ( nm_usuario_p text) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w		timestamp := null;



BEGIN

select	to_date(max(to_char(clock_timestamp(),'dd/mm/yyyy')||' '||lpad(hr_inicio,2,0)||':'||substr(hr_inicio,3,4)||':00'), 'dd/mm/yyyy hh24:mi:ss')
into STRICT 	dt_retorno_w
from	man_horario_trabalho
where	to_char(clock_timestamp(),'d')=ie_dia_semana
and	man_obter_se_hor_trab_lib(nr_sequencia,null,null,nm_usuario_p)='S';

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
