SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_idioma_os_local ( nr_seq_ordem_servico_p bigint) RETURNS bigint AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade: Obter o idioma da ordem de serviço comforme a localização/CNPJ referenciados
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[ X ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
nr_seq_localizacao_w			man_ordem_servico.nr_seq_localizacao%type;
cd_cnpj_w				man_localizacao.cd_cnpj%type;
nr_seq_idioma_w				pessoa_juridica.nr_seq_idioma%type;


BEGIN
if (nr_seq_ordem_servico_p IS NOT NULL AND nr_seq_ordem_servico_p::text <> '') then
	select	nr_seq_localizacao
	into STRICT	nr_seq_localizacao_w
	from	man_ordem_servico
	where	nr_sequencia	= nr_seq_ordem_servico_p;

	if (nr_seq_localizacao_w IS NOT NULL AND nr_seq_localizacao_w::text <> '') then
		select	max(a.cd_cnpj)
		into STRICT	cd_cnpj_w
		from	man_localizacao	a
		where	a.nr_sequencia	= nr_seq_localizacao_w;

		if (cd_cnpj_w IS NOT NULL AND cd_cnpj_w::text <> '') then
			select	max(a.nr_seq_idioma)
			into STRICT	nr_seq_idioma_w
			from	pessoa_juridica	a
			where	a.cd_cgc	= cd_cnpj_w;
		end if;
	end if;
end if;

return nr_seq_idioma_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION man_obter_idioma_os_local ( nr_seq_ordem_servico_p bigint) FROM PUBLIC;

