SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_leg_status_acordo (nr_seq_ordem_serv_p bigint) RETURNS varchar AS $body$
DECLARE


retorno_w	varchar(10);


BEGIN

	/*
		Verifica o status do acordo:
			R = Recusada
			I = Incluída
			A = Aceita
	*/
	select 	max(ie_status_acordo)
	into STRICT 	retorno_w
	from   	desenv_acordo_os
	where  	nr_seq_ordem_servico = nr_seq_ordem_serv_p;

	/* Irá retornar o valor da legenda ( shift+f5)  conforme o status encontrado */

	if (retorno_w = 'R')then
		retorno_w := '1401'; -- Clips vermelho
	end if;

	if (retorno_w = 'I')then
		retorno_w := '1402'; --Clips amarelo
	end if;

	if (retorno_w = 'A')then
		retorno_w := '1403'; --Clips verde
	end if;

return	coalesce(retorno_w, '');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
