			'@ds_tipo_equip = ' || wheb_mensagem_pck.get_texto(301649,null) || ds_enter_w || -- Tipo do equipamento
			'@ds_localizacao = ' || wheb_mensagem_pck.get_texto(301650,null) || ds_enter_w || -- Localização do equipamento
			'@ds_propriedade = ' || wheb_mensagem_pck.get_texto(301651,null) || ds_enter_w || -- Propriedade do equipamento
			'@ds_situacao_atual = ' || wheb_mensagem_pck.get_texto(301652,null) || ds_enter_w || -- Situação atual do equipamento
			'@ds_grupo_planej_atual = ' || wheb_mensagem_pck.get_texto(301653,null) || ds_enter_w || -- Grupo de planejamento atual do equipamento
			'@ds_grupo_trab_atual = ' || wheb_mensagem_pck.get_texto(301654,null) || ds_enter_w || -- Grupo de trabalho atual do equipamento
			'@ds_local_setor_atual = ' || wheb_mensagem_pck.get_texto(301655,null),1,2000); -- Localização e setor da localização atual do equipamento
	if (ie_tipo_p = '15') then
		ds_retorno_w := substr(ds_retorno_w || ds_enter_w || '@ds_local_setor_ant = ' || wheb_mensagem_pck.get_texto(301656,null),1,2000); -- Localização e setor da localização anterior do equipamento
	end if;
	end;
elsif (ie_tipo_p = 16) then
	ds_retorno_w := substr('@plano_inspecao = ' || wheb_mensagem_pck.get_texto(301657,null),1,2000); -- Descrição do plano inspeção
end if;

return ds_retorno_w;

END;
