CREATE OR REPLACE FUNCTION man_obter_min_exec_desenv_sup ( nr_seq_os_p bigint) RETURNS bigint AS $body$
DECLARE


ds_retorno_w	bigint;


BEGIN
select	sum(coalesce(a.qt_minuto,0))
into STRICT	ds_retorno_w
from	man_ordem_serv_ativ a
where	a.nr_seq_ordem_serv       = nr_seq_os_p
and	a.nr_seq_funcao not in (31, 32, 311,11,551,132,132)
and	exists (SELECT 1 from usuario_grupo_des u
		where u.nm_usuario_grupo = a.nm_usuario_exec);

/* 142|Conexão
 462|Orientação do desenvolvimento
  22|Suporte
 */
return	coalesce(ds_retorno_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
