CREATE OR REPLACE FUNCTION man_obter_min_ordem_prev_usu ( nr_seq_ordem_serv_p bigint, nm_usuario_prev_p text, dt_prevista_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_retorno_w			double precision;


BEGIN

select	coalesce(sum(qt_min_prev),0) qt_minuto
into STRICT	qt_retorno_w
from	man_ordem_ativ_prev a
where	nr_seq_ordem_serv	= nr_seq_ordem_serv_p
and	nm_usuario_prev		= nm_usuario_prev_p
and	dt_prevista between trunc(dt_prevista_p,'dd') and fim_dia(dt_prevista_p);

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
