CREATE OR REPLACE FUNCTION man_obter_min_os_execucao ( nr_seq_ordem_p bigint, dt_referencia_p timestamp, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


qt_retorno_w		bigint;
dt_atividade_w		timestamp;
qt_realizado_w		bigint;
qt_parcial_w		bigint;


BEGIN


select	coalesce(max(dt_atividade),clock_timestamp())
into STRICT	dt_atividade_w
from	man_ordem_serv_ativ
where	nr_seq_ordem_serv	= nr_seq_ordem_p
and	nm_usuario_exec		= nm_usuario_p
and	trunc(dt_atividade)	= trunc(dt_referencia_p)
and	coalesce(dt_fim_atividade::text, '') = '';

select	coalesce(sum(qt_minuto),0)
into STRICT	qt_realizado_w
from	man_ordem_serv_ativ
where	nr_seq_ordem_serv	= nr_seq_ordem_p
and	nm_usuario_exec		= nm_usuario_p
and	dt_atividade > trunc(dt_referencia_p,'dd')
and	dt_atividade < dt_atividade_w;

select	obter_min_entre_datas(dt_atividade_w,dt_referencia_p,1)
into STRICT	qt_parcial_w
;

select	qt_parcial_w + qt_realizado_w
into STRICT	qt_retorno_w
;

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
