CREATE OR REPLACE FUNCTION man_obter_min_pendentes ( nm_usuario_p text) RETURNS double precision AS $body$
DECLARE


qt_min_pendentes_w		bigint	:= 0;
qt_usuarios_w			bigint	:= 0;
qt_retorno_w			double precision	:= 0;


BEGIN

select	dividir(sum(a.qt_min_pend_prev),60)
into STRICT	qt_min_pendentes_w
from	w_desempenho_equipe	a
where	a.nm_usuario		= nm_usuario_p;

select	count(*)
into STRICT	qt_usuarios_w
from	usuario_grupo_des;

qt_usuarios_w	:= qt_usuarios_w * 9;
qt_retorno_w	:= dividir(qt_min_pendentes_w, qt_usuarios_w);

return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
