CREATE OR REPLACE FUNCTION man_obter_min_real_usuario ( nr_seq_ordem_serv_p bigint, nm_usuario_exec_p text, dt_atividade_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_retorno_w			double precision;


BEGIN

select coalesce(sum(qt_minuto),0) qt_minuto
into STRICT	qt_retorno_w
from	(
	SELECT	coalesce(sum(qt_minuto),0) qt_minuto
	from	man_ordem_serv_ativ
	where	nr_seq_ordem_serv	= nr_seq_ordem_serv_p
	and		nm_usuario_exec		= nm_usuario_exec_p
	
union

	SELECT	((clock_timestamp() - dt_atividade) *24*60) qt_minuto
	from	man_ordem_serv_ativ
	where	nr_seq_ordem_serv	= nr_seq_ordem_serv_p
	and		nm_usuario_exec		= nm_usuario_exec_p
	and 	coalesce(dt_fim_atividade::text, '') = '') alias8;


return	qt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
