CREATE OR REPLACE FUNCTION man_obter_min_sla_os_estagio ( nr_seq_ordem_p bigint, ie_tempo_p text, nr_seq_estagio_p bigint) RETURNS bigint AS $body$
DECLARE


qt_min_real_os_w		bigint;
dt_ordem_w		timestamp;
dt_inicio_w		timestamp;
dt_fim_w			timestamp;
cd_estabelecimento_w	bigint;
dt_estagio_w		timestamp;
nr_seq_estagio_w		bigint := nr_seq_estagio_p;
nr_seq_estagio_ww		bigint;


BEGIN

begin
qt_min_real_os_w		:= 0;

Select	a.dt_ordem_servico,
	coalesce(a.dt_inicio_real, a.dt_ordem_servico),
	coalesce(a.dt_fim_real, clock_timestamp()),
	b.cd_estabelecimento,
	nr_seq_estagio
into STRICT	dt_ordem_w,
	dt_inicio_w,
	dt_fim_w,
	cd_estabelecimento_w,
	nr_seq_estagio_ww
from	man_localizacao b,
	man_ordem_servico a
where	a.nr_sequencia		= nr_seq_ordem_p
and	a.nr_seq_localizacao	= b.nr_sequencia;

if (coalesce(nr_seq_estagio_ww,0) > 0) and (coalesce(nr_seq_estagio_ww,0) <> nr_seq_estagio_w) then
	nr_seq_estagio_w := nr_seq_estagio_ww;
end if;

select 	max(dt_atualizacao)
into STRICT	dt_estagio_w
from	man_ordem_serv_estagio
where	nr_seq_estagio 	= nr_seq_estagio_p
and	nr_seq_ordem	= nr_seq_ordem_p;

if (ie_tempo_p = 'COM') then
	select 	Man_Obter_min_Com(cd_estabelecimento_w, dt_estagio_w, clock_timestamp())
	into STRICT	qt_min_real_os_w
	;
else
	qt_min_real_os_w	:= (dt_inicio_w - dt_estagio_w) * 1440;
end if;

exception
	when others then
	qt_min_real_os_w		:= 0;
end;
Return qt_min_real_os_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
