SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_min_sla_os ( nr_seq_ordem_p bigint, ie_tempo_p text) RETURNS bigint AS $body$
DECLARE


qt_min_real_os_w		bigint;
qt_min_w			bigint;
dt_atualizacao_w		timestamp;
dt_ordem_w		timestamp;
dt_inicio_w		timestamp;
dt_fim_w			timestamp;
dt_anterior_w		timestamp;
ie_aguarda_cliente_w	varchar(15);
ie_aguarda_cliente_ww	varchar(15);
cd_estabelecimento_w	bigint;

C01 CURSOR FOR
	SELECT a.dt_atualizacao, b.ie_aguarda_cliente
	from	Man_estagio_processo b,
		Man_ordem_serv_estagio a
	where	a.nr_seq_ordem		= nr_seq_ordem_p
	and	a.nr_seq_estagio		= b.nr_sequencia
	
union all

	SELECT coalesce(a.dt_fim_real,clock_timestamp()),
		b.ie_aguarda_cliente
	from	Man_estagio_processo b,
		Man_ordem_servico a
	where	a.nr_sequencia		= nr_seq_ordem_p
	and	a.nr_seq_estagio		= b.nr_sequencia
	order by 1;


BEGIN

begin
qt_min_real_os_w		:= 0;
Select	a.dt_ordem_servico,
	coalesce(a.dt_inicio_real, a.dt_ordem_servico),
	coalesce(a.dt_fim_real, clock_timestamp()),
	b.cd_estabelecimento
into STRICT	dt_ordem_w,
	dt_inicio_w,
	dt_fim_w,
	cd_estabelecimento_w
from	man_localizacao b,
	man_ordem_servico a
where	a.nr_sequencia		= nr_seq_ordem_p
and	a.nr_seq_localizacao	= b.nr_sequencia;

if (ie_tempo_p = 'COM') then
	select 	Man_Obter_min_Com(cd_estabelecimento_w, dt_ordem_w, dt_inicio_w)
	into STRICT	qt_min_real_os_w
	;
else
	qt_min_real_os_w	:= (dt_inicio_w - dt_ordem_w) * 1440;
end if;

ie_aguarda_cliente_ww	:= 'N';
dt_anterior_w			:= dt_inicio_w;

OPEN C01;
LOOP
FETCH C01 into
	dt_atualizacao_w,
	ie_aguarda_cliente_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	qt_min_w		:= 0;
	if (ie_aguarda_cliente_ww = 'N') and (dt_atualizacao_w > dt_inicio_w) then
		begin
		if (ie_tempo_p = 'COM') then
			select 	Man_Obter_min_Com(cd_estabelecimento_w, dt_anterior_w, dt_atualizacao_w)
			into STRICT	qt_min_w
			;
		else
			qt_min_w	:= (dt_atualizacao_w - dt_anterior_w) * 1440;
		end if;
		qt_min_real_os_w	:= qt_min_real_os_w + qt_min_w;
		end;
	end if;
	ie_aguarda_cliente_ww	:= ie_aguarda_cliente_w;
	dt_anterior_w		:= dt_atualizacao_w;
	end;
END LOOP;
CLOSE C01;

exception
	when others then
	qt_min_real_os_w		:= 0;
end;
Return qt_min_real_os_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
