CREATE OR REPLACE FUNCTION man_obter_origem_defeito (nr_seq_ordem_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1):= 'X';
ie_origem_erro_w 	varchar(15);
ie_possui_doc_w 	bigint;


BEGIN
/*
(W,C,O,N,E)(Philips,Cliente,Distribuidores, Philips consultoria,Externo)
X = NÃ£o tem Doc defeito

*/
if (coalesce(nr_seq_ordem_p,0) > 0) then
begin
	select	count(*)
	into STRICT	ie_possui_doc_w
	from	man_doc_erro
	where	nr_seq_ordem = nr_seq_ordem_p
	and 	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');


	if (ie_possui_doc_w > 0)	then
		begin
		select  max(ie_origem_erro)
		into STRICT 	ie_retorno_w
		from 	man_doc_erro
		where 	nr_seq_ordem = nr_seq_ordem_p;

		end;
	else
		ie_retorno_w	:= 'X';
	end if;

end;
end if;


return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
