CREATE OR REPLACE FUNCTION man_obter_paciente_ordem_serv ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

			 
cd_pessoa_fisica_w	varchar(10);


BEGIN 
 
if (coalesce(nr_sequencia_p,0) > 0) then 
	begin 
	select	max(c.cd_pessoa_fisica) 
	into STRICT	cd_pessoa_fisica_w 
	from	ocupacao_unidade_v c, 
		man_localizacao b, 
		man_ordem_servico a 
	where	b.nr_sequencia		= a.nr_seq_localizacao	 
	and	b.cd_setor		= c.cd_setor_atendimento 
	and	b.cd_unidade_basica	= to_char(c.nr_seq_interno) 
	and	a.nr_sequencia		= nr_sequencia_p;
	end;
end if;
 
return	cd_pessoa_fisica_w;
 
end;
