CREATE OR REPLACE FUNCTION man_obter_perc_def_fora ( nr_seq_gerencia_p bigint, nr_seq_grupo_des_p bigint, qt_dia_util_p bigint, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE

 
pr_defeito_ant_w		integer;
dt_referencia_w			timestamp;
qt_total_w			bigint;
qt_anterior_w			bigint;
nr_seq_grupo_des_w		bigint;


BEGIN 
 
pr_defeito_ant_w	:= 0;
nr_seq_grupo_des_w	:= coalesce(nr_seq_grupo_des_p,0);
 
/*Obter o Ãºltimo dia util, de acordo com a quantidade informada*/
 
begin 
 
select	obter_dia_util_ant_periodo(clock_timestamp(),qt_dia_util_p,cd_estabelecimento_p) 
into STRICT	dt_referencia_w
;
 
exception when others then 
 
	select	trunc(clock_timestamp() - qt_dia_util_p) 
	into STRICT	dt_referencia_w 
	;
 
end;
 
select	count(*) 
into STRICT	qt_total_w 
from 	man_ordem_servico_v a, 
   	man_ordem_serv_sla  b 
where 	a.nr_sequencia = b.nr_seq_ordem 
and  	b.nr_seq_status <> 412 
and	a.nr_seq_gerencia_des = nr_seq_gerencia_p 
and	((a.nr_seq_grupo_des = nr_seq_grupo_des_w) or (nr_seq_grupo_des_w = 0)) 
and (obter_se_os_pend_cliente(b.nr_seq_ordem) = 'N') 
and (Obter_Se_OS_Desenv(b.nr_seq_ordem) = 'S') 
and	a.ie_classificacao = 'E' 
and  	ie_status_ordem <> 3;
 
if (qt_total_w > 0) then 
	begin 
 
	select	count(*) 
	into STRICT	qt_anterior_w 
	from 	man_ordem_servico_v a, 
	   	man_ordem_serv_sla  b 
	where 	a.nr_sequencia = b.nr_seq_ordem 
	and	a.dt_ordem_servico < dt_referencia_w 
	and  	b.nr_seq_status <> 412 
	and	a.nr_seq_gerencia_des = nr_seq_gerencia_p 
	and	((a.nr_seq_grupo_des = nr_seq_grupo_des_w) or (nr_seq_grupo_des_w = 0)) 
	and (obter_se_os_pend_cliente(b.nr_seq_ordem) = 'N') 
	and (Obter_Se_OS_Desenv(b.nr_seq_ordem) = 'S') 
	and	a.ie_classificacao = 'E' 
	and  	ie_status_ordem <> 3;
 
	pr_defeito_ant_w	:= ((qt_anterior_w * 100) / qt_total_w);
 
	end;
end if;
 
return	pr_defeito_ant_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
