CREATE OR REPLACE FUNCTION man_obter_perc_erro_os (dt_referencia_p timestamp, cd_empresa_p bigint) RETURNS bigint AS $body$
DECLARE



dt_referencia_w			timestamp;
dt_fim_mes_w			timestamp;
qt_total_os_w			bigint;
qt_erro_os_w			bigint;
pr_erro_os_w			double precision;


BEGIN

dt_referencia_w			:= trunc(dt_referencia_p,'month');
dt_fim_mes_w			:= last_day(dt_referencia_w) + 86399/86400;

select 	count(*),
	sum(CASE WHEN ie_classificacao='E' THEN 1  ELSE 0 END ) qt_erro
into STRICT	qt_total_os_w,
	qt_erro_os_w
from	man_ordem_servico a
where	dt_ordem_servico between dt_referencia_w and dt_fim_mes_w
and	exists (
	SELECT	1
	from	estabelecimento b,
		man_localizacao c
	where	a.nr_seq_localizacao	= c.nr_sequencia
	and	c.cd_estabelecimento	= b.cd_estabelecimento
	and	b.cd_empresa		= cd_empresa_p);

pr_erro_os_w	:= dividir(qt_erro_os_w * 100, qt_total_os_w);

RETURN pr_erro_os_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
