CREATE OR REPLACE FUNCTION man_obter_peso_prior_regra ( ie_prioridade_p text) RETURNS bigint AS $body$
DECLARE


ds_retorno_w	integer := 0;
nr_seq_regra_w	bigint;


BEGIN
if (ie_prioridade_p IS NOT NULL AND ie_prioridade_p::text <> '') then
	begin
	select	nr_sequencia
	into STRICT	nr_seq_regra_w
	from	man_regra_peso_prior
	where	ie_prioridade = ie_prioridade_p  LIMIT 1;
	exception
	when others then
		nr_seq_regra_w := 0;
	end;

	if (nr_seq_regra_w > 0) then
		select	qt_peso
		into STRICT	ds_retorno_w
		from	man_regra_peso_prior
		where	nr_sequencia = nr_seq_regra_w;
	end if;
end if;

return coalesce(ds_retorno_w,0);

end;
$body$
