CREATE OR REPLACE FUNCTION man_obter_prazo_prioridade ( nr_seq_equipamento_p bigint, ie_prioridade_p text, nr_seq_grupo_trab_p bigint, nr_seq_grupo_planej_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* IE_OPCAO_P
D - Descrição do prazo (Ex.: 3 dia(s));

Foi criado este parâmetro com apenas uma opção para que, caso no futuro se deseje buscar apenas a quantidade,
sem descrição, baste realizar o tratamento do IE_OPCAO_P
*/
qt_existe_w			bigint;
nr_seq_tipo_equip_w		bigint;
qt_dias_w			man_regra_dias_prioridade.qt_dias%type;
qt_horas_w			man_regra_dias_prioridade.qt_horas%type;
ds_retorno_w			varchar(255);

c01 CURSOR FOR
SELECT	a.qt_dias,
	a.qt_horas
FROM man_regra_dias_prioridade a
LEFT OUTER JOIN man_regra_dias_prior_lib b ON (a.nr_sequencia = b.nr_seq_regra_dia_prior)
WHERE coalesce(b.nr_seq_equipamento,coalesce(nr_seq_equipamento_p,0))		= coalesce(nr_seq_equipamento_p,0) and coalesce(b.nr_seq_tipo_equipamento,coalesce(nr_seq_tipo_equip_w,0))	= coalesce(nr_seq_tipo_equip_w,0) and coalesce(b.nr_seq_grupo_trabalho,coalesce(nr_seq_grupo_trab_p,0)) 	= coalesce(nr_seq_grupo_trab_p,0) and coalesce(a.ie_prioridade,coalesce(ie_prioridade_p,0)) 			= coalesce(ie_prioridade_p,0) and coalesce(b.nr_seq_grupo_planej,coalesce(nr_seq_grupo_planej_p,0))		= coalesce(nr_seq_grupo_planej_p,0) and a.ie_situacao = 'A' order by
	coalesce(b.nr_seq_equipamento,0),
	coalesce(b.nr_seq_tipo_equipamento,0);


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	man_regra_dias_prioridade
where	ie_prioridade = ie_prioridade_p;

if (qt_existe_w > 0) then
	begin

	select	max(nr_seq_tipo_equip)
	into STRICT	nr_seq_tipo_equip_w
	from	man_equipamento
	where	nr_sequencia = nr_seq_equipamento_p;

	open C01;
	loop
	fetch C01 into
		qt_dias_w,
		qt_horas_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;

	if (coalesce(qt_horas_w,0) = 0) then
		begin

		if (ie_prioridade_p = 'B') then
			ds_retorno_w	:= to_char(coalesce(qt_dias_w,30)) || ' dia(s)';
		elsif (ie_prioridade_p = 'M') then
			ds_retorno_w	:= to_char(coalesce(qt_dias_w,14)) || ' dia(s)';
		elsif (ie_prioridade_p = 'A') then
			ds_retorno_w	:= to_char(coalesce(qt_dias_w,7)) || ' dia(s)';
		elsif (ie_prioridade_p = 'U') then
			ds_retorno_w	:= to_char(coalesce(qt_dias_w,3)) || ' dia(s)';
		elsif (ie_prioridade_p = 'E') then
			begin

			if (coalesce(qt_dias_w,0) = 0) then
				ds_retorno_w	:= obter_desc_expressao(344644);--'No dia';
			else
				ds_retorno_w	:= to_char(qt_dias_w) || ' dia(s)';
			end if;

			end;
		elsif (ie_prioridade_p = 'S') then
			begin

			if (coalesce(qt_dias_w,0) = 0) then
				ds_retorno_w	:= 'N/A';
			else
				ds_retorno_w	:= to_char(qt_dias_w) || ' dia(s)';
			end if;

			end;
		end if;

		end;
	else
		ds_retorno_w	:= to_char(qt_horas_w) || obter_desc_expressao(730584);--' hora(s)';
	end if;


	end;
else
	begin

	if (ie_prioridade_p = 'B') then
		ds_retorno_w	:= '30 dia(s)';
	elsif (ie_prioridade_p = 'M') then
		ds_retorno_w	:= '14 dia(s)';
	elsif (ie_prioridade_p = 'A') then
		ds_retorno_w	:= '7 dia(s)';
	elsif (ie_prioridade_p = 'U') then
		ds_retorno_w	:= '3 dia(s)';
	elsif (ie_prioridade_p = 'E') then
		ds_retorno_w	:= wheb_mensagem_pck.get_texto(303135);
	elsif (ie_prioridade_p = 'S') then
		ds_retorno_w	:= 'N/A';
	end if;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
