CREATE OR REPLACE FUNCTION man_obter_primeiro_exec_setor (nr_sequencia_p bigint, cd_setor_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(15);
dt_primeiro_w		timestamp;


BEGIN
select	min(a.nm_usuario_exec)
into STRICT	ds_retorno_w
from	usuario b,
	man_ordem_servico_exec a
where	a.nr_seq_ordem		= nr_sequencia_p
and	b.nm_usuario		= a.nm_usuario_exec
and	b.cd_setor_atendimento 	= cd_setor_atendimento_p;

if (coalesce(ds_retorno_w,'X') = 'X') then
	select	nm_usuario_exec
	into STRICT	ds_retorno_w
	from	man_ordem_servico
	where	nr_sequencia = nr_sequencia_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
