CREATE OR REPLACE FUNCTION man_obter_prioridade_os_equip ( ie_parado_p text, nr_seq_equipamento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1);
cd_impacto_w		varchar(1);

C01 CURSOR FOR
	SELECT	ie_prioridade
	from	man_regra_prior_equip_os
	where	coalesce(ie_parado,ie_parado_p) = ie_parado_p
	and	coalesce(cd_impacto,cd_impacto_w) = cd_impacto_w
	and	clock_timestamp() between coalesce(dt_inicio_vigencia,clock_timestamp()) and coalesce(dt_fim_vigencia,clock_timestamp())
	order by ie_parado desc,
		cd_impacto desc,
		nr_sequencia;


BEGIN

if (coalesce(ie_parado_p,'0') <> '0') and (coalesce(nr_seq_equipamento_p,0) > 0) then
	begin
	select	coalesce(max(cd_impacto),'0')
	into STRICT	cd_impacto_w
	from	man_equipamento
	where	nr_sequencia = nr_seq_equipamento_p;

	open C01;
	loop
	fetch C01 into
		ie_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;
	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
