CREATE OR REPLACE FUNCTION man_obter_prior_dt_concl_desej ( dt_conclusao_p timestamp, dt_ordem_servico_p timestamp) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
qt_dias_w	bigint;


BEGIN

if (dt_conclusao_p IS NOT NULL AND dt_conclusao_p::text <> '') and (dt_ordem_servico_p IS NOT NULL AND dt_ordem_servico_p::text <> '') then
	begin
	select	obter_dias_entre_datas(dt_ordem_servico_p, dt_conclusao_p)
	into STRICT	qt_dias_w
	;

	if (qt_dias_w < 1) then
		ds_retorno_w := 'E';
	elsif (qt_dias_w between 1 and 3) then
		ds_retorno_w := 'U';
	elsif (qt_dias_w between 3 and 7) then
		ds_retorno_w := 'A';
	elsif (qt_dias_w between 7 and 14) then
		ds_retorno_w := 'M';
	elsif (qt_dias_w between 14 and 30) then
		ds_retorno_w := 'B';
	elsif (qt_dias_w > 30) then
		ds_retorno_w := 'S';
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
