SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_qtd_horas (dt_inicial_p timestamp, dt_final_p timestamp, ie_opcao_p bigint, cd_cnpj_p bigint) RETURNS bigint AS $body$
DECLARE


QT_MINUTO_w	bigint;
retorno_w	double precision;


BEGIN
/*
0 - Suporte
1 - Desenvolvimento
*/
if (ie_opcao_p = 0) then

	select 	sum(QT_MINUTO)
	into STRICT	QT_MINUTO_w
	from	MAN_ORDEM_SERV_ATIV a,
		usuario b,
		man_ordem_servico c,
		man_localizacao d
	where	a.NM_USUARIO_EXEC 	= b.nm_usuario
	and	a.nr_seq_ordem_serv 	= c.nr_sequencia
	and	c.nr_seq_localizacao	= d.nr_sequencia
	and	b.CD_SETOR_ATENDIMENTO 	= 4
	and	a.DT_ATIVIDADE between dt_inicial_p and fim_dia(dt_final_p)
	and	d.cd_cnpj		= cd_cnpj_p;

else
	select 	sum(QT_MINUTO)
	into STRICT	QT_MINUTO_w
	from	MAN_ORDEM_SERV_ATIV a,
		usuario b,
		man_ordem_servico c,
		man_localizacao d
	where	a.NM_USUARIO_EXEC 	= b.nm_usuario
	and	a.nr_seq_ordem_serv 	= c.nr_sequencia
	and	c.nr_seq_localizacao	= d.nr_sequencia
	and	b.CD_SETOR_ATENDIMENTO 	= 2
	and	a.DT_ATIVIDADE between dt_inicial_p and fim_dia(dt_final_p)
	and	d.cd_cnpj		= cd_cnpj_p;

end if;

retorno_w	:= trunc((QT_MINUTO_w/60),2);

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
