CREATE OR REPLACE FUNCTION man_obter_qt_dia_estagio_os ( nr_seq_ordem_serv_p bigint) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w		bigint;
dt_atual_w		timestamp;
dt_atualizacao_w		timestamp;
qt_retorno_w		bigint := 0;


BEGIN

if (coalesce(nr_seq_ordem_serv_p,0) > 0) then
	begin
	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	man_ordem_serv_estagio
	where	nr_seq_ordem = nr_seq_ordem_serv_p;

	if (coalesce(nr_sequencia_w,0) > 0) then
		begin
		select	dt_atualizacao
		into STRICT	dt_atualizacao_w
		from	man_ordem_serv_estagio
		where	nr_sequencia = nr_sequencia_w;

		select	coalesce(dt_fim_real,clock_timestamp())
		into STRICT	dt_atual_w
		from	man_ordem_servico
		where	nr_sequencia = nr_seq_ordem_serv_p;

		qt_retorno_w := obter_dias_entre_datas(dt_atualizacao_w,dt_atual_w);
		end;
	end if;
	end;
end if;

return	qt_retorno_w;

end;
$body$
