CREATE OR REPLACE FUNCTION man_obter_qt_ordem_periodo ( cd_pessoa_fisica_p bigint, nr_seq_equipamento_p bigint, ie_classificacao_p text, dt_inicial_p timestamp, dt_final_p timestamp, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/* ie_opcao_p
Q - Quantidade
P - Percentual
*/
qt_erro_w		bigint;
qt_erro_total_w		bigint;
vl_retorno_w		double precision;
dt_inicial_w		timestamp;
dt_final_w		timestamp;


BEGIN

dt_inicial_w	:= dt_inicial_p;
dt_final_w	:= fim_dia(dt_final_p);

select	count(*)
into STRICT	qt_erro_w
from	man_ordem_servico
where	dt_ordem_servico between dt_inicial_w and dt_final_w
and	((coalesce(ie_classificacao_p,'X') = 'X') or (ie_classificacao = ie_classificacao_p))
and	((cd_pessoa_fisica_p = 0) or (cd_pessoa_solicitante = cd_pessoa_fisica_p))
and	((nr_seq_equipamento_p = 0) or (nr_seq_equipamento = nr_seq_equipamento_p));

if (ie_opcao_p = 'Q') then
	vl_retorno_w	:= qt_erro_w;
else
	begin

	select	count(*)
	into STRICT	qt_erro_total_w
	from	man_ordem_servico
	where	dt_ordem_servico between dt_inicial_w and dt_final_w
	and	((coalesce(ie_classificacao_p,'X') = 'X') or (ie_classificacao = ie_classificacao_p))
	and	((nr_seq_equipamento_p = 0) or (nr_seq_equipamento = nr_seq_equipamento_p));

	vl_retorno_w	:= dividir((qt_erro_w * 100),qt_erro_total_w);

	end;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
