SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_qt_os_periodo (dt_referencia_p timestamp, nr_seq_gerencia_p bigint, nr_seq_grupo_p bigint, nm_usuario_p text, ie_periodo_p text) RETURNS bigint AS $body$
DECLARE


qt_os_w			bigint	:= null;
ie_desenv_tec_w		varchar(1)	:= null;
qt_total_tec_w		bigint	:= null;
dt_mes_ref_w		timestamp;
dt_fim_mes_w		timestamp;


BEGIN

if (dt_referencia_p IS NOT NULL AND dt_referencia_p::text <> '') then
	dt_mes_ref_w	:= trunc(dt_referencia_p,'month');
	dt_fim_mes_w	:= fim_dia(last_day(dt_referencia_p));

	if (ie_periodo_p	= 'M') then
		if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
			/* Verifica se o usuário é da Tecnologia, Desenvolvimento ou nenhum dos dois */

			select	CASE WHEN count(*)=0 THEN 'N'  ELSE CASE WHEN b.nr_seq_gerencia=2 THEN 'T'  ELSE 'D' END  END
			into STRICT	ie_desenv_tec_w
			from	grupo_desenvolvimento b,
				usuario_grupo_des a
			where	a.nr_seq_grupo		= b.nr_sequencia
			and	a.nm_usuario_grupo	= nm_usuario_p;

			select	count(*)
			into STRICT	qt_os_w
			from	man_ordem_servico a
			where	a.dt_ordem_servico between dt_mes_ref_w and dt_fim_mes_w
			and	exists (SELECT	1
					from	man_ordem_servico_exec x
					where	x.nr_seq_ordem	= a.nr_sequencia
					and	x.nm_usuario_exec = nm_usuario_p
					and	coalesce(x.dt_fim_execucao::text, '') = '')
			and (ie_desenv_tec_w = 'N' or exists (select	1
									from	man_estagio_processo y,
										man_ordem_serv_estagio x
									where	x.nr_seq_estagio	= y.nr_sequencia
									and	x.nr_seq_ordem		= a.nr_sequencia
									and	CASE WHEN ie_desenv_tec_w='D' THEN y.ie_desenv  ELSE y.ie_tecnologia END  = 'S')
				);

			qt_total_tec_w	:= 0;

			/* Se for tecnologia tem que somar as OS's que passaram por ela */

			if (ie_desenv_tec_w = 'T') then
				select	count(*) qt_total_os
				into STRICT	qt_total_tec_w
				from	grupo_desenvolvimento b,
					man_ordem_servico a
				where	a.dt_ordem_servico between dt_mes_ref_w and dt_fim_mes_w
				and	a.nr_seq_grupo_des	= b.nr_sequencia
				and	b.nr_seq_gerencia	!= 2
				and	exists (
					SELECT 	1
					from	man_ordem_serv_ativ c,
						grupo_desenvolvimento d,
						usuario_grupo_des e
					where	a.nr_sequencia = c.nr_seq_ordem_serv
					and	d.nr_seq_gerencia = 2
					and	e.nr_seq_grupo = d.nr_sequencia
					and	c.nm_usuario_exec = e.nm_usuario_grupo
					and	c.nm_usuario_exec = nm_usuario_p)
				and 	exists (
					SELECT 	1
					from	man_ordem_serv_tecnico	c,
						grupo_desenvolvimento d,
						usuario_grupo_des e
					where	a.nr_sequencia = c.nr_seq_ordem_serv
					and	d.nr_seq_gerencia = 2
					and	e.nr_seq_grupo = d.nr_sequencia
					and	c.nm_usuario = e.nm_usuario_grupo
					and	e.nm_usuario_grupo = nm_usuario_p)
				and	exists (
					select	1
					from	man_ordem_serv_estagio f,
						man_estagio_processo g
					where	f.nr_seq_ordem = a.nr_sequencia
					and	g.nr_sequencia = f.nr_seq_estagio
					and	g.ie_tecnologia = 'S');
			end if;

			qt_os_w	:= qt_os_w + qt_total_tec_w;
		elsif (nr_seq_grupo_p IS NOT NULL AND nr_seq_grupo_p::text <> '') then
			/* Verifica se o grupo é da Tecnologia, Desenvolvimento ou nenhum dos dois */

			select	CASE WHEN count(*)=0 THEN 'N'  ELSE CASE WHEN b.nr_seq_gerencia=2 THEN 'T'  ELSE 'D' END  END
			into STRICT	ie_desenv_tec_w
			from	grupo_desenvolvimento b
			where	b.nr_sequencia	= nr_seq_grupo_p;

			select	count(*)
			into STRICT	qt_os_w
			from	man_ordem_servico a
			where	a.dt_ordem_servico between dt_mes_ref_w and dt_fim_mes_w
			and (ie_desenv_tec_w = 'N' or a.nr_seq_grupo_des	= nr_seq_grupo_p)
			and (ie_desenv_tec_w = 'N' and a.nr_seq_grupo_sup	= nr_seq_grupo_p)
			and (ie_desenv_tec_w = 'N' or exists (SELECT	1
									from	man_estagio_processo y,
										man_ordem_serv_estagio x
									where	x.nr_seq_estagio	= y.nr_sequencia
									and	x.nr_seq_ordem		= a.nr_sequencia
									and	CASE WHEN ie_desenv_tec_w='D' THEN y.ie_desenv  ELSE y.ie_tecnologia END  = 'S')
				);

			qt_total_tec_w	:= 0;

			/* Se for tecnologia tem que somar as OS's que passaram por ela */

			if (ie_desenv_tec_w = 'T') then
				select	count(*) qt_total_os
				into STRICT	qt_total_tec_w
				from	grupo_desenvolvimento b,
					man_ordem_servico a
				where	a.dt_ordem_servico between dt_mes_ref_w and dt_fim_mes_w
				and	a.nr_seq_grupo_des	= b.nr_sequencia
				and	a.nr_seq_grupo_des	= nr_seq_grupo_p
				and	b.nr_seq_gerencia	!= 2
				and	exists (
					SELECT 	1
					from	man_ordem_serv_ativ c,
						grupo_desenvolvimento d,
						usuario_grupo_des e
					where	a.nr_sequencia = c.nr_seq_ordem_serv
					and	d.nr_seq_gerencia = 2
					and	e.nr_seq_grupo = d.nr_sequencia
					and	c.nm_usuario_exec = e.nm_usuario_grupo)
				and 	exists (
					SELECT 	1
					from	man_ordem_serv_tecnico	c,
						grupo_desenvolvimento d,
						usuario_grupo_des e
					where	a.nr_sequencia = c.nr_seq_ordem_serv
					and	d.nr_seq_gerencia = 2
					and	e.nr_seq_grupo = d.nr_sequencia
					and	c.nm_usuario = e.nm_usuario_grupo)
				and	exists (
					select	1
					from	man_ordem_serv_estagio f,
						man_estagio_processo g
					where	f.nr_seq_ordem = a.nr_sequencia
					and	g.nr_sequencia = f.nr_seq_estagio
					and	g.ie_tecnologia = 'S');
			end if;

			qt_os_w	:= qt_os_w + qt_total_tec_w;
		elsif (nr_seq_gerencia_p IS NOT NULL AND nr_seq_gerencia_p::text <> '') then
			/* Verifica se a gerência é da Tecnologia, Desenvolvimento ou nenhum dos dois */

			select	CASE WHEN 2='T' THEN 					3 WHEN 2='D' THEN 					4 WHEN 2='D' THEN 					9 WHEN 2='D' THEN 					7 WHEN 2='D' THEN 					'N' END
			into STRICT	ie_desenv_tec_w
			from	gerencia_wheb b
			where	b.nr_sequencia	= nr_seq_gerencia_p;

			select	count(*)
			into STRICT	qt_os_w
			from	grupo_desenvolvimento b,
				man_ordem_servico a
			where	a.dt_ordem_servico between dt_mes_ref_w and dt_fim_mes_w
			and	a.nr_seq_grupo_des	= b.nr_sequencia
			and	b.nr_seq_gerencia	= nr_seq_gerencia_p
			and (ie_desenv_tec_w = 'N' or exists (SELECT	1
									from	man_estagio_processo y,
										man_ordem_serv_estagio x
									where	x.nr_seq_estagio	= y.nr_sequencia
									and	x.nr_seq_ordem		= a.nr_sequencia
									and	CASE WHEN ie_desenv_tec_w='D' THEN y.ie_desenv  ELSE y.ie_tecnologia END  = 'S')
				);

			qt_total_tec_w	:= 0;

			/* Se for tecnologia tem que somar as OS's que passaram por ela */

			if (ie_desenv_tec_w = 'T') then
				select	count(*) qt_total_os
				into STRICT	qt_total_tec_w
				from	grupo_desenvolvimento b,
					man_ordem_servico a
				where	a.dt_ordem_servico between dt_mes_ref_w and dt_fim_mes_w
				and	a.nr_seq_grupo_des	= b.nr_sequencia
				and	b.nr_seq_gerencia	!= 2
				and	exists (
					SELECT 	1
					from	man_ordem_serv_ativ c,
						grupo_desenvolvimento d,
						usuario_grupo_des e
					where	a.nr_sequencia = c.nr_seq_ordem_serv
					and	d.nr_seq_gerencia = 2
					and	e.nr_seq_grupo = d.nr_sequencia
					and	c.nm_usuario_exec = e.nm_usuario_grupo)
				and 	exists (
					SELECT 	1
					from	man_ordem_serv_tecnico	c,
						grupo_desenvolvimento d,
						usuario_grupo_des e
					where	a.nr_sequencia = c.nr_seq_ordem_serv
					and	d.nr_seq_gerencia = 2
					and	e.nr_seq_grupo = d.nr_sequencia
					and	c.nm_usuario = e.nm_usuario_grupo)
				and	exists (
					select	1
					from	man_ordem_serv_estagio f,
						man_estagio_processo g
					where	f.nr_seq_ordem = a.nr_sequencia
					and	g.nr_sequencia = f.nr_seq_estagio
					and	g.ie_tecnologia = 'S');
			end if;
		end if;
	end if;
end if;

return	qt_os_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION man_obter_qt_os_periodo (dt_referencia_p timestamp, nr_seq_gerencia_p bigint, nr_seq_grupo_p bigint, nm_usuario_p text, ie_periodo_p text) FROM PUBLIC;

