CREATE OR REPLACE FUNCTION man_obter_quest_os_philips ( nr_seq_ordem_servico_p bigint, ie_origem_necessidade_os_p text, ie_tipo_necessidade_os_p text) RETURNS bigint AS $body$
DECLARE


nr_seq_modelo_os_w		bigint;
ie_classificacao_w			varchar(01);
cd_funcao_w			integer;
ie_origem_necessidade_os_w		varchar(15);
ie_tipo_necessidade_os_w		varchar(15);

c01 CURSOR FOR
SELECT	nr_seq_modelo_os
from	regra_quest_os_philips
where	coalesce(cd_funcao, coalesce(cd_funcao_w,0)) 			= coalesce(cd_funcao_w,0)
and	coalesce(ie_classificacao, ie_classificacao_w) 		= ie_classificacao_w
and	coalesce(ie_origem_necessidade_os,ie_origem_necessidade_os_w)= ie_origem_necessidade_os_w
and	coalesce(ie_tipo_necessidade_os,ie_tipo_necessidade_os_w) 	= ie_tipo_necessidade_os_w
and	clock_timestamp() between coalesce(dt_inicio_vigencia,clock_timestamp()) and coalesce(dt_fim_vigencia,clock_timestamp())
order by	coalesce(ie_tipo_necessidade_os,0),
	coalesce(ie_origem_necessidade_os,0);



BEGIN
if (nr_seq_ordem_servico_p > 0) then
	ie_origem_necessidade_os_w	:= coalesce(ie_origem_necessidade_os_p,'X');
	ie_tipo_necessidade_os_w	:= coalesce(ie_tipo_necessidade_os_p,'X');

	select	ie_classificacao,
		cd_funcao
	into STRICT	ie_classificacao_w,
		cd_funcao_w
	from	man_ordem_servico
	where	nr_sequencia = nr_seq_ordem_servico_p;

	open C01;
	loop
	fetch C01 into
		nr_seq_modelo_os_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		null;
		end;
	end loop;
	close C01;
end if;

return	nr_seq_modelo_os_w;

end;
$body$
