CREATE OR REPLACE FUNCTION man_obter_regra_controle_equip ( nr_seq_equipamento_p bigint) RETURNS bigint AS $body$
DECLARE



cd_estabelecimento_w	smallint := wheb_usuario_pck.get_cd_estabelecimento;
cd_centro_custo_w		integer;
nr_seq_localizacao_w	bigint;
nr_seq_tipo_equip_w	bigint;
nr_seq_grupo_planej_w	bigint;
nr_seq_grupo_trab_w	bigint;
nr_seq_regra_w		bigint;


c01 CURSOR FOR
SELECT	nr_sequencia
from	man_regra_controle_equip
where	cd_estabelecimento = cd_estabelecimento_w
and	ie_situacao = 'A'
and	coalesce(nr_seq_localizacao,nr_seq_localizacao_w) = nr_seq_localizacao_w
and	coalesce(cd_centro_custo, cd_centro_custo_w) = cd_centro_custo_w
and	coalesce(nr_seq_tipo_equip, nr_seq_tipo_equip_w) = nr_seq_tipo_equip_w
and	coalesce(nr_seq_grupo_trab, nr_seq_grupo_trab_w) = nr_seq_grupo_trab_w
and	coalesce(nr_seq_grupo_planej, nr_seq_grupo_planej_w) = nr_seq_grupo_planej_w
order by	coalesce(nr_seq_grupo_trab,0),
	coalesce(nr_seq_grupo_planej,0),
	coalesce(cd_centro_custo,0),
	coalesce(nr_seq_localizacao,0),
	coalesce(nr_seq_tipo_equip,0);



BEGIN

if (nr_seq_equipamento_p > 0) then

	select	nr_seq_tipo_equip,
		nr_seq_local,
		cd_centro_custo,
		nr_seq_trab,
		nr_seq_planej
	into STRICT	nr_seq_tipo_equip_w,
		nr_seq_localizacao_w,
		cd_centro_custo_w,
		nr_seq_grupo_trab_w,
		nr_seq_grupo_planej_w
	from	man_equipamento
	where	nr_sequencia = nr_seq_equipamento_p;

	open C01;
	loop
	fetch C01 into
		nr_seq_regra_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		nr_seq_regra_w := nr_seq_regra_w;
		end;
	end loop;
	close C01;

end if;

return	nr_seq_regra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
