SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_regra_determ_risco ( ie_probability_harm_p text, ie_severity_harm_p text ) RETURNS varchar AS $body$
DECLARE

ds_risco_w 	varchar(255);
ie_acceptable_w	man_regra_determ_risco.ie_acceptable%type;

BEGIN

	if (ie_probability_harm_p IS NOT NULL AND ie_probability_harm_p::text <> '' AND ie_severity_harm_p IS NOT NULL AND ie_severity_harm_p::text <> '') then
		select  max(ie_acceptable)
                into STRICT    ie_acceptable_w
		from    man_regra_determ_risco
		where   ie_situacao = 'A'
		and     ie_severity_harm        = ie_severity_harm_p
		and     ie_probability_harm     = ie_probability_harm_p;

		ds_risco_w := case when coalesce(ie_acceptable_w::text, '') = '' then 'N' else ie_acceptable_w end;
	
	else
		ds_risco_w := null;
	end if;
	
	return ds_risco_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
