CREATE OR REPLACE FUNCTION man_obter_regra_rel_pend ( nr_seq_localizacao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(15)	:= 'G';


BEGIN
if (nr_seq_localizacao_p IS NOT NULL AND nr_seq_localizacao_p::text <> '') then
	begin

	select	coalesce(max(ie_regra),'G')
	into STRICT	ds_retorno_w
	from	man_regra_rel_pend_philips
	where	ie_situacao = 'A'
	and	nr_seq_localizacao = nr_seq_localizacao_p;

	end;
end if;

return ds_retorno_w;

end;
$body$
