CREATE OR REPLACE FUNCTION man_obter_regra_tempo_sla ( nr_seq_regra_p bigint, ie_prioridade_p text) RETURNS varchar AS $body$
DECLARE


ie_tempo_w				varchar(15);
ds_tempo_w				varchar(255) := '';


BEGIN

select	coalesce(max(ie_tempo),'X')
into STRICT		ie_tempo_w
from		man_sla			b,
			man_sla_regra	a
where	b.nr_sequencia	= nr_seq_regra_p
and		a.nr_seq_sla		= b.nr_sequencia
and		a.ie_prioridade	= ie_prioridade_p;

if (ie_tempo_w <> 'X') then
	ds_tempo_w	:= obter_valor_dominio(1646,ie_tempo_w);
end if;

return	ds_tempo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
