SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_resp_quest ( nr_sequencia_p bigint, nr_seq_perg_p bigint, ie_tipo_retorno text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(4000);
ie_componente_w		varchar(15);


BEGIN
if	(nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '' AND nr_seq_perg_p IS NOT NULL AND nr_seq_perg_p::text <> '') then

	select	substr(coalesce(coalesce(ds_resultado, dt_resultado), vl_resultado),1,4000) ds_resposta
	into STRICT	ds_retorno_w
	from	man_ordem_servico_resposta
	where	nr_seq_ordem = nr_sequencia_p
	and	nr_seq_pergunta = nr_seq_perg_p;

if (ie_tipo_retorno = 'D') then

	select	ie_componente
	into STRICT	ie_componente_w
	from	pergunta
	where	nr_sequencia = nr_seq_perg_p;

	if	(ie_componente_w = 'cb' AND ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '')then
		select	CASE WHEN ds_retorno_w='S' THEN wheb_mensagem_pck.get_texto(305052)  ELSE wheb_mensagem_pck.get_texto(305053) END
		into STRICT	ds_retorno_w
		;
	end if;

	if	(ie_componente_w = 'lcb' AND ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '')then

		select	max(ds_item)
		into STRICT	ds_retorno_w
		from	pergunta_result
		where	cd_item = ds_retorno_w
		and	nr_seq_pergunta = nr_seq_perg_p;

	end if;

end if;


end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
