CREATE OR REPLACE FUNCTION man_obter_seq_ult_hist ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE



nr_sequencia_w		bigint;
/*Function utilizada na atualização das ordens de serviço via WS - Projeto XML 1*/

BEGIN
if (coalesce(nr_sequencia_p,0) > 0) then
	select	max(a.nr_sequencia)
	into STRICT	nr_sequencia_w
	from	man_ordem_servico b,
		man_ordem_serv_tecnico a
	where	a.nr_seq_ordem_serv 	= b.nr_sequencia
	and	a.ie_origem 		= 'I'
	and (a.dt_atualizacao > b.dt_envio_wheb OR coalesce(b.dt_envio_wheb::text, '') = '')
	and 	a.nr_seq_ordem_serv 	= nr_sequencia_p;
end if;

return	nr_sequencia_w;

end;
