CREATE OR REPLACE FUNCTION man_obter_se_calibracao_aprov (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(01) := 'N';
qt_erro_total_w		double precision;
qt_media_leitura_w		double precision;
vl_saida_w		double precision;
qt_incerteza_medicao_w	double precision;
qt_variavel_w		double precision := 0;
qt_erro_max_permitido_w	double precision;


BEGIN

select	obter_qt_erro_total_calibracao(l.nr_sequencia),
	l.qt_media_leitura,
	l.vl_saida,
	l.qt_incerteza_medicao,
	e.qt_erro_max_permitido
into STRICT	qt_erro_total_w,
	qt_media_leitura_w,
	vl_saida_w,
	qt_incerteza_medicao_w,
	qt_erro_max_permitido_w
from	man_equip_calib_escala e,
	man_calibracao l
where	l.nr_sequencia	= nr_sequencia_p
and	e.nr_sequencia	= l.nr_seq_equip_calib_escala;

/*Se não tiver informado o erro total calcula com a formula abaixo*/

if (coalesce(qt_erro_total_w,0) = 0) then
	qt_erro_total_w	:= coalesce(vl_saida_w,0) - coalesce(qt_media_leitura_w,0);
end if;

/*Variavel a ser considerada sobre o erro max permitido (para mais ou para menos, por isso o abs no if abaixo) */

qt_variavel_w	:= (coalesce(qt_erro_total_w,0));

/*verifica se a variavel é menor que o erro máximo*/

if (qt_variavel_w <= qt_erro_max_permitido_w) then
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
