SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_se_cat_serv_lib_usu ( nr_seq_servico_p bigint, nm_usuario_p text, cd_setor_usuario_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(01) := 'S';
cd_estabelecimento_w		smallint;
ie_permissao_cat_serv_w		varchar(01);
qt_existe_w			bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	man_catalogo_setor
where	nr_seq_cs = nr_seq_servico_p;

select	cd_estabelecimento
into STRICT	cd_estabelecimento_w
from	man_catalogo_servico
where	nr_sequencia = nr_seq_servico_p;

if (qt_existe_w = 0) then
	ds_retorno_w	:= 'S';
elsif (qt_existe_w > 0) then
	begin
	select	substr(coalesce(obter_valor_param_usuario(299, 158, Obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w),'N'),1,1)
	into STRICT	ie_permissao_cat_serv_w
	;

	if (ie_permissao_cat_serv_w = 'T') then
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ds_retorno_w
		from	man_catalogo_setor
		where	nr_seq_cs 		= nr_seq_servico_p
		and (substr(obter_se_setor_usuario(cd_setor_atendimento, nm_usuario_p),1,1) = 'S');
	elsif (ie_permissao_cat_serv_w = 'U') and (coalesce(cd_setor_usuario_p,0) > 0) then
		select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
		into STRICT	ds_retorno_w
		from	man_catalogo_setor
		where	nr_seq_cs 		= nr_seq_servico_p
		and	cd_setor_atendimento	= cd_setor_usuario_p;
	else
		ds_retorno_w	:= ie_permissao_cat_serv_w;
	end if;
	end;
end if;

return	coalesce(ds_retorno_w,'N');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
