CREATE OR REPLACE FUNCTION man_obter_se_causa_dano_lib ( nr_seq_causa_p bigint, nr_seq_grupo_trab_p bigint, nr_seq_grupo_planej_p bigint, nr_seq_tipo_ordem_p bigint default 0) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'S';
qt_existe_w	bigint;



BEGIN

/*Verifica se existe alguma regra*/

select	count(*)
into STRICT	qt_existe_w
from	man_causa_dano_lib
where	nr_seq_causa_dano = nr_seq_causa_p;

if (qt_existe_w > 0) then
	begin
	select	'S'
	into STRICT	ds_retorno_w
	from	man_causa_dano_lib
	where	nr_seq_causa_dano 				= nr_seq_causa_p
	and (coalesce(nr_seq_grupo_trab,coalesce(nr_seq_grupo_trab_p,0)) 	= coalesce(nr_seq_grupo_trab_p,0))
	and (coalesce(nr_seq_grupo_planej,coalesce(nr_seq_grupo_planej_p,0)) 	= coalesce(nr_seq_grupo_planej_p,0))
	and (coalesce(nr_seq_tipo_ordem,coalesce(nr_seq_tipo_ordem_p,0))	= coalesce(nr_seq_tipo_ordem_p,0))  LIMIT 1;
	exception
	when others then
		ds_retorno_w := 'N';
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
