CREATE OR REPLACE FUNCTION man_obter_se_equip_grupo_trab ( nr_seq_equipamento_p bigint, nr_grupo_trabalho_p bigint) RETURNS varchar AS $body$
DECLARE


ie_retorno_w				varchar(1)	:= 'N';
nr_seq_trab_w				man_equipamento.nr_seq_trab%type;



BEGIN
if (coalesce(nr_seq_equipamento_p,0) <> 0) and (coalesce(nr_grupo_trabalho_p,0) <> 0) then
	ie_retorno_w	:= 'N';

	select	nr_seq_trab
	into STRICT	nr_seq_trab_w
	from	man_equipamento
	where	nr_sequencia	= nr_seq_equipamento_p;

	if (nr_seq_trab_w = nr_grupo_trabalho_p) then
		ie_retorno_w	:= 'S';
	else

		select	coalesce(max('S'),'N')
		into STRICT	ie_retorno_w
		from	man_equip_grupo_trab
		where	nr_seq_equipamento	= nr_seq_equipamento_p
		and	nr_seq_trab		= nr_grupo_trabalho_p;
	end if;

end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
