SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_se_equip_lib_usuario ( nr_seq_equipamento_p bigint, nm_usuario_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(01) := 'N';
qt_existe_w		bigint;
cd_perfil_w		integer := obter_perfil_ativo;
cd_cargo_w		bigint;

/*
ie_opcao_p
V - Identifica se visualiza (se não tiver regra, ou a regra atende ao usuário do parâmetro);
E - Identifica se permite encerrar a OS deste equipamento (consistindo as regras);
*/
BEGIN

/*Verifica se existe alguma regra para o equipamento*/

select	count(*)
into STRICT	qt_existe_w
from	man_equip_usuario_excl
where	nr_seq_equipamento = nr_seq_equipamento_p;

if (qt_existe_w = 0) then
	ds_retorno_w := 'S';
else
	begin
	/*Se tiver alguma regra em branco, esta liberado*/

	begin
	select	'S'
	into STRICT	ds_retorno_w
	from	man_equip_usuario_excl
	where	nr_seq_equipamento = nr_seq_equipamento_p
	and	coalesce(cd_perfil_exclusivo::text, '') = ''
	and	coalesce(nm_usuario_exclusivo::text, '') = ''
	and	coalesce(cd_cargo_exclusivo::text, '') = ''
	and	CASE WHEN ie_opcao_p='E' THEN ie_permite_encerrar_os  ELSE 'S' END  = 'S'  LIMIT 1;
	exception
	when others then
		ds_retorno_w := 'N';
	end;

	if (ds_retorno_w = 'N') then
		begin

		begin
		select	'S'
		into STRICT	ds_retorno_w
		from	man_equip_usuario_excl
		where	nr_seq_equipamento 	= nr_seq_equipamento_p
		and	nm_usuario_exclusivo	= nm_usuario_p
		and	CASE WHEN ie_opcao_p='E' THEN ie_permite_encerrar_os  ELSE 'S' END  = 'S'  LIMIT 1;
		exception
		when others then
			ds_retorno_w := 'N';
		end;

		if (ds_retorno_w = 'N') then
			begin
			select	'S'
			into STRICT	ds_retorno_w
			from	man_equip_usuario_excl
			where	nr_seq_equipamento 	= nr_seq_equipamento_p
			and	cd_perfil_exclusivo	= cd_perfil_w
			and	CASE WHEN ie_opcao_p='E' THEN ie_permite_encerrar_os  ELSE 'S' END  = 'S'  LIMIT 1;
			exception
			when others then
				ds_retorno_w := 'N';
			end;
		end if;

		if (ds_retorno_w = 'N') then
			begin
			select	coalesce(max(b.cd_cargo),0)
			into STRICT	cd_cargo_w
			from	usuario a,
				pessoa_fisica b
			where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
			and	a.nm_usuario		= nm_usuario_p;

			begin
			select	'S'
			into STRICT	ds_retorno_w
			from	man_equip_usuario_excl
			where	nr_seq_equipamento 	= nr_seq_equipamento_p
			and	cd_cargo_exclusivo	= cd_cargo_w
			and	CASE WHEN ie_opcao_p='E' THEN ie_permite_encerrar_os  ELSE 'S' END  = 'S'  LIMIT 1;
			exception
			when others then
				ds_retorno_w := 'N';
			end;
			end;
		end if;

		end;
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
