CREATE OR REPLACE FUNCTION man_obter_se_escala_email_exec ( dt_referencia_p timestamp, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1);


BEGIN
select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END
into STRICT	ie_retorno_w
from	man_ordem_serv_ativ	b,
	man_ordem_servico	a
where	b.nr_seq_ordem_serv	= a.nr_sequencia
and	a.ie_escala_email_sup	= 'S'
and	b.nm_usuario_exec	= nm_usuario_p
and	dt_referencia_p between b.dt_atividade and coalesce(b.dt_fim_atividade,dt_referencia_p);

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
