CREATE OR REPLACE FUNCTION man_obter_se_estagio_processo ( nr_seq_estagio_p bigint, nr_seq_grupo_trab_p bigint, nm_usuario_p text, cd_setor_atendimento_p bigint, nr_seq_grupo_planej_p bigint) RETURNS varchar AS $body$
DECLARE


/* cd_setor_atendimento_p
Passar o parametro cd_setor_atendimento_p somente se for o caso de buscar pelo setor em que o usuario esta logado,
caso contrario o sistema identifica o setor pelo setor no cadastro do usuario
*/
ds_retorno_w			varchar(01)	:= 'S';
cd_setor_atendimento_w		integer;
nr_seq_grupo_trab_w		bigint;
nr_seq_grupo_planej_w		bigint;
qt_regra_w			bigint;
cd_perfil_w			perfil.cd_perfil%type;


BEGIN

select	coalesce(max(nr_seq_grupo_trab),0),
	coalesce(max(nr_seq_grupo_planej),0)
into STRICT	nr_seq_grupo_trab_w,
	nr_seq_grupo_planej_w
from	man_estagio_processo
where	nr_sequencia	= nr_seq_estagio_p;

if (nr_seq_grupo_trab_w > 0) then
	begin
	if (nr_seq_grupo_trab_w <> nr_seq_grupo_trab_p) then
		ds_retorno_w	:= 'N';
	end if;
	end;
elsif (nr_seq_grupo_planej_w > 0) then
	begin
	if (nr_seq_grupo_planej_w <> nr_seq_grupo_planej_p) then
		ds_retorno_w	:= 'N';
	end if;
	end;
else
	begin
	cd_setor_atendimento_w	:= cd_setor_atendimento_p;
	if (coalesce(cd_setor_atendimento_p, 0) = 0) then
		select	coalesce(max(cd_setor_atendimento),0)
		into STRICT	cd_setor_atendimento_w
		from	usuario
		where	nm_usuario	= nm_usuario_p;
	end if;

	cd_perfil_w	:= obter_perfil_ativo;

	select	count(*)
	into STRICT	qt_regra_w
	from	man_estagio_regra
	where	nr_seq_estagio	= nr_seq_estagio_p;

	if (qt_regra_w > 0) then
		begin
		select	count(*)
		into STRICT	qt_regra_w
		from	man_estagio_regra
		where	nr_seq_estagio		= nr_seq_estagio_p
		and	coalesce(cd_setor_atendimento,coalesce(cd_setor_atendimento_w,0)) = coalesce(cd_setor_atendimento_w,0)
		and	coalesce(cd_perfil,coalesce(cd_perfil_w,0)) = coalesce(cd_perfil_w,0)
		and	coalesce(nr_seq_grupo_trab,coalesce(nr_seq_grupo_trab_p,0))	= coalesce(nr_seq_grupo_trab_p,0);

		if (qt_regra_w = 0) then
			ds_retorno_w	:= 'N';
		end if;
		end;
	end if;
	end;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
