SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION man_obter_se_exec_rota_insp ( nm_usuario_p text, nr_seq_rota_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1) := 'S';
qt_existe_w		bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	man_rota_insp_exec
where	nr_seq_rota_inspecao = nr_seq_rota_p;

if (coalesce(nm_usuario_p,'X') <> 'X') and (coalesce(nr_seq_rota_p,0) <> 0) and (coalesce(ie_opcao_p,'X') <> 'X') and (qt_existe_w > 0) then
	begin
	select	count(*)
	into STRICT	qt_existe_w
	from	man_rota_insp_exec
	where	nr_seq_rota_inspecao = nr_seq_rota_p
	and (coalesce(nr_seq_grupo_planej::text, '') = '' and
		coalesce(nr_seq_grupo_trabalho::text, '') = '');

	if (qt_existe_w = 0) then
		if (ie_opcao_p = 'S') then /* Grupo de Planejamento e Trabalho */
			begin
			select	count(*)
			into STRICT	qt_existe_w
			from	man_rota_insp_exec
			where	nr_seq_rota_inspecao = nr_seq_rota_p
			and (nr_seq_grupo_planej IS NOT NULL AND nr_seq_grupo_planej::text <> '' AND nr_seq_grupo_trabalho IS NOT NULL AND nr_seq_grupo_trabalho::text <> '');

			if (qt_existe_w > 0) then
				begin
				select	'S'
				into STRICT	ds_retorno_w
				from	man_rota_insp_exec e,
					man_grupo_trab_usuario d,
					man_grupo_trabalho c,
					man_grupo_planej_trab b,
					man_grupo_planejamento a
				where	a.nr_sequencia = b.nr_seq_grupo_planej
				and	c.nr_sequencia = b.nr_seq_grupo_trab
				and	c.nr_sequencia = d.nr_seq_grupo_trab
				and	e.nr_seq_grupo_planej = a.nr_sequencia
				and	e.nr_seq_grupo_trabalho = c.nr_sequencia
				and	d.nm_usuario_param = nm_usuario_p
				and	e.nr_seq_rota_inspecao = nr_seq_rota_p  LIMIT 1;
				exception
				when others then
					ds_retorno_w := 'N';
				end;
			end if;
			end;
		elsif (ie_opcao_p = 'P') then /* Grupo de Planejamento */
			begin
			select	count(*)
			into STRICT	qt_existe_w
			from	man_rota_insp_exec
			where	nr_seq_rota_inspecao = nr_seq_rota_p
			and	(nr_seq_grupo_planej IS NOT NULL AND nr_seq_grupo_planej::text <> '');

			if (qt_existe_w > 0) then
				begin
				select	'S'
				into STRICT	ds_retorno_w
				from	man_rota_insp_exec e,
					man_grupo_trab_usuario d,
					man_grupo_trabalho c,
					man_grupo_planej_trab b,
					man_grupo_planejamento a
				where	a.nr_sequencia = b.nr_seq_grupo_planej
				and	c.nr_sequencia = b.nr_seq_grupo_trab
				and	c.nr_sequencia = d.nr_seq_grupo_trab
				and	e.nr_seq_grupo_planej = a.nr_sequencia
				and	d.nm_usuario_param = nm_usuario_p
				and	e.nr_seq_rota_inspecao = nr_seq_rota_p  LIMIT 1;
				exception
				when others then
					ds_retorno_w := 'N';
				end;
			end if;
			end;
		elsif (ie_opcao_p = 'T') then /* Grupo de Trabalho */
			begin
			select	count(*)
			into STRICT	qt_existe_w
			from	man_rota_insp_exec
			where	nr_seq_rota_inspecao = nr_seq_rota_p
			and	(nr_seq_grupo_trabalho IS NOT NULL AND nr_seq_grupo_trabalho::text <> '');

			if (qt_existe_w > 0) then
				begin
				select	'S'
				into STRICT	ds_retorno_w
				from	man_rota_insp_exec e,
					man_grupo_trab_usuario d,
					man_grupo_trabalho c,
					man_grupo_planej_trab b,
					man_grupo_planejamento a
				where	a.nr_sequencia = b.nr_seq_grupo_planej
				and	c.nr_sequencia = b.nr_seq_grupo_trab
				and	c.nr_sequencia = d.nr_seq_grupo_trab
				and	e.nr_seq_grupo_trabalho = c.nr_sequencia
				and	d.nm_usuario_param = nm_usuario_p
				and	e.nr_seq_rota_inspecao = nr_seq_rota_p  LIMIT 1;
				exception
				when others then
					ds_retorno_w := 'N';
				end;
			end if;
			end;
		end if;
	end if;
	end;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
