CREATE OR REPLACE FUNCTION man_obter_se_gera_os_fixo ( nr_sequencia_p bigint, dt_referencia_p timestamp, nr_seq_equip_p bigint, dt_prev_geracao_os_p timestamp) RETURNS varchar AS $body$
DECLARE


ie_retorno_w		varchar(1) := 'N';


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) and (coalesce(nr_seq_equip_p,0) > 0) and (dt_referencia_p IS NOT NULL AND dt_referencia_p::text <> '') then
	begin

	begin
	select	'S'
	into STRICT	ie_retorno_w
	from	man_regra_data_frequencia a
	where	coalesce(a.ie_data_hora,'D') = 'D'
	and	trunc(a.dt_geracao,'dd') = trunc(dt_referencia_p,'dd')
	and	coalesce(a.nr_seq_equipamento,nr_seq_equip_p) = nr_seq_equip_p
	and	a.nr_seq_planej_prev = nr_sequencia_p  LIMIT 1;
	exception
	when others then
		ie_retorno_w := 'N';
	end;

	if (ie_retorno_w = 'N') then
		begin

		begin
		select	'S'
		into STRICT	ie_retorno_w
		from	man_regra_data_frequencia a
		where	a.ie_data_hora = 'S'
		and	a.dt_dia_semana = pkg_date_utils.get_WeekDay(dt_referencia_p)
		and	coalesce(a.nr_seq_equipamento,nr_seq_equip_p) = nr_seq_equip_p
		and	a.nr_seq_planej_prev = nr_sequencia_p  LIMIT 1;
		exception
		when others then
			ie_retorno_w := 'N';
		end;

		if (ie_retorno_w = 'N') then
			begin
			select	'S'
			into STRICT	ie_retorno_w
			from	man_freq_planej c,
				man_planej_prev b,
				man_regra_data_frequencia a
			where	b.nr_sequencia = a.nr_seq_planej_prev
			and	c.nr_sequencia = b.nr_seq_frequencia
			and	a.ie_data_hora = 'H'
			and	(a.hr_geracao IS NOT NULL AND a.hr_geracao::text <> '')
			and	trunc(coalesce(a.dt_geracao,dt_referencia_p),'dd') = trunc(dt_referencia_p,'dd')
			and	coalesce(a.dt_dia_semana,pkg_date_utils.get_WeekDay(dt_referencia_p)) = pkg_date_utils.get_WeekDay(dt_referencia_p)
			and	(trunc(clock_timestamp()) >= trunc(dt_prev_geracao_os_p)
				or (not exists (	SELECT	1
						from	man_regra_data_frequencia x
						where	((coalesce(x.ie_data_hora,'D') = 'D' and (x.dt_geracao IS NOT NULL AND x.dt_geracao::text <> ''))
							or (x.ie_data_hora = 'S' and (x.dt_dia_semana IS NOT NULL AND x.dt_dia_semana::text <> '')))
						and	coalesce(x.nr_seq_equipamento,nr_seq_equip_p) = nr_seq_equip_p
						and	x.nr_seq_planej_prev = b.nr_sequencia) and c.ie_fixo_data = 'S'))
			and	coalesce(a.nr_seq_equipamento,nr_seq_equip_p) = nr_seq_equip_p
			and	b.nr_sequencia = nr_sequencia_p  LIMIT 1;
			exception
			when others then
				ie_retorno_w := 'N';
			end;
		end if;
		end;
	end if;
	end;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
