CREATE OR REPLACE FUNCTION man_obter_se_hor_trab_lib ( nr_sequencia_p bigint, nr_seq_grupo_planej_p bigint, nr_seq_grupo_trab_p bigint, nm_usuario_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(01) := 'N';
qt_existe_w		bigint	:= 0;


  horario_trab RECORD;

BEGIN
for horario_trab in (	SELECT	nr_seq_grupo_trab,
				nr_seq_grupo_planej,
				nm_usuario_lib
			from	man_horario_trabalho_lib
			where	nr_seq_horario_trab	= nr_sequencia_p
			and	ie_situacao = 'A') loop
	begin
	qt_existe_w	:= 1;

	if (horario_trab.nr_seq_grupo_trab = nr_seq_grupo_trab_p) then
		ds_retorno_w	:= 'S';
	end if;

	if (horario_trab.nr_seq_grupo_planej = nr_seq_grupo_planej_p) then
		ds_retorno_w	:= 'S';
	end if;

	if (horario_trab.nm_usuario_lib = nm_usuario_p) then
		ds_retorno_w	:= 'S';
	end if;

	if (coalesce(horario_trab.nm_usuario_lib::text, '') = '') and (coalesce(horario_trab.nr_seq_grupo_planej::text, '') = '') and (coalesce(horario_trab.nr_seq_grupo_trab::text, '') = '') then
		ds_retorno_w	:= 'S';
	end if;

	if (ds_retorno_w = 'S') then
		exit;
	end if;
	end;
end loop;

if (qt_existe_w = 0) then
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
